import React, { useEffect, useState } from 'react';
import axios from 'axios';

function DataComponent() {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true); // A単adir un estado para el loading

  useEffect(() => {
    const authenticateAndFetchData = async () => {
      try {
        // 1. Hacer la solicitud de login
        const authResponse = await axios.post('http://localhost:8080/api/auth/login', {
          username: 'admin', // Cambia por tu usuario
          password: 'admin'  // Cambia por tu contrase単a
        });

        // Obtener el token del login (supongo que se encuentra en authResponse.data.token)
        const token = authResponse.data.token;

        // 2. Usar el token para la solicitud de datos
        const dataResponse = await axios.get('http://localhost:8080/api/business', {
          headers: {
            'Authorization': `Bearer ${token}`, // Cambiado a Bearer token
          },
          withCredentials: true,
        });

        setData(dataResponse.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching data:', err);
        setError('Error fetching data');
        setLoading(false);
      }
    };

    authenticateAndFetchData();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Data from Java API</h1>
      <pre>{JSON.stringify(data, null, 2)}</pre>
    </div>
  );
}

export default DataComponent;
