import React, { useEffect, useState } from 'react';
import axios from 'axios';

function DataComponent() {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const username = 'user'; // Cambia por tu usuario
    const password = 'admin123'; // Cambia por tu contraseña
    const token = btoa(`${username}:${password}`); // Codificar en Base64

    const requestOptions = {
      headers: {
        'Authorization': `Basic ${token}`, // Enviar el header de Basic Auth
      },
      withCredentials: true, // Si necesitas enviar cookies
    };

    axios
      .get('http://localhost:8080/api/business', requestOptions)
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
        setError('Error fetching data');
      });
  }, []);

  if (error) return <div>{error}</div>;
  if (!data) return <div>Loading...</div>;

  return (
    <div>
      <h1>Data from Java API</h1>
      <pre>{JSON.stringify(data, null, 2)}</pre>
    </div>
  );
}

export default DataComponent;
